/* eslint-disable max-len */
import { Injectable } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Recipe } from './recipe/recipe.model';

@Injectable({
  providedIn: 'root'
})
export class RecipeService {

  recipeList: Recipe[] = [
    {
      id: 'Jollof237',
      title: 'Jollof Rice',
      imageUrl: 'data:image/jpeg;base64,',
      ingredients: ['Rice', 'Tomatoes', 'Onions']
    },
    {
      id: 'Koki453',
      title: 'Koki',
      imageUrl: 'data:image/jpeg;base64,',
      ingredients: ['Koki beans', 'Palm Oil', 'Plantain Leaves']
    },
    {
      id: 'Ekwang419',
      title: 'Ekwang',
      imageUrl: 'data:image/jpeg;base64,',
      ingredients: ['Cocoyams', 'Cocoyam Leaves', 'Njanga']
    }
  ];

  recipeForm: FormGroup =     new FormGroup({
    id: new FormControl('', [Validators.required]),
    title: new FormControl('', [Validators.required]),
    imageUrl: new FormControl(''),
    ingredients: new FormControl()
  });

  constructor(private router: Router) { }

  submitRecipe(){
    const recipe: Recipe = {
      id: this.recipeForm.get('id').value,
      title: this.recipeForm.get('title').value,
      imageUrl: this.recipeForm.get('imageUrl').value,
      ingredients: this.recipeForm.get('ingredients').value
    };
    this.recipeList.push(recipe);
    this.clearRecipeForm();
  }

  clearRecipeForm(){
    this.recipeForm.reset();
  }

  getRecipeDetails(recipeId: string){
    return this.recipeList.find(recipe => {
       {return recipe.id === recipeId;}
      });
  }

  deleteRecipe(recipeId: string){
    this.recipeList = this.recipeList.filter(recipe => recipe.id !== recipeId);
    this.router.navigate(['/recipe']);
  }
}
